INSERT INTO `wp_posts` VALUES (109, 1, '2013-09-06 02:29:38', '2013-09-06 00:29:38', '<div class="tresc" style="text-align: left;"></div>\r\n<div class="tresc" style="text-align: left;"></div>\r\n<div class="tresc" style="text-align: left;"><span style="font-size: 16px;">• Dowiesz się co oznacza workflow i jakie może mieć zastosowanie w organizacji.</span></div>\r\n<div class="tresc" style="text-align: left;"><span style="font-size: 16px;"> • Zyskasz pełniejsze zrozumienie najlepszych innowacji oraz zmian w sferze obiegu dokumentów oraz pracy grupowej.</span></div>\r\n<div class="tresc" style="text-align: left;"><span style="font-size: 16px;"> • Poznasz aspekty prawne w zakresie ochrony informacji oraz dokumentów.</span></div>\r\n<div class="tresc" style="text-align: left;"><span style="font-size: 16px;"> • Znajdziesz więcej oszczędnych sposobów na optymalizację procesów biznesowych.</span></div>\r\n<div class="tresc" style="text-align: left;"><span style="font-size: 16px;"> • Poznasz workflow jako rozwiązanie opłacalne, zapewniające organizacji pełne wykorzystanie posiadanych zasobów.</span></div>\r\n<div class="tresc" style="text-align: left;"><span style="font-size: 16px;"> • Zdobędziesz wiedzę o nowych możliwościach oraz innowacjach platformowych dla swojej firmy.</span></div>\r\n<div class="tresc" style="text-align: left;"><span style="font-size: 16px;"> • Poznasz fakty i dysfunkcje w zarządzaniu dokumentacją przedsiębiorstwa.</span></div>', 'DLACZEGO WARTO WZIĄĆ UDZIAŁ ?', '', 'publish', 'open', 'open', '', 'dlaczego-warto', '', '', '2013-09-11 09:32:31', '2013-09-11 07:32:31', '', 0, 'http://workflowtrends.pl/?page_id=109', 0, 'page', '', 0); 
INSERT INTO `wp_posts` VALUES (110, 1, '2013-09-06 02:29:38', '2013-09-06 00:29:38', '', 'DLACZEGO WARTO', '', 'inherit', 'open', 'open', '', '109-revision-v1', '', '', '2013-09-06 02:29:38', '2013-09-06 00:29:38', '', 109, 'http://workflowtrends.pl/?p=110', 0, 'revision', '', 0); 
INSERT INTO `wp_posts` VALUES (111, 1, '2013-09-06 02:30:24', '2013-09-06 00:30:24', '<p style="font-family: Impact; font-size: 14px; text-align: center;"><span style="font-family: impact, chicago; font-size: large;">Konferencje</span>  <span style="text-align: justify; font-family: Georgia, \'Times New Roman\', \'Bitstream Charter\', Times, serif; font-size: 13px;">dają możliwość szerokiej promocji firmy oraz oferty produktowej przed wyselekcjonowanym audytorium słuchaczy, odpowiedzialnych za zakup i wdrażanie nowych rozwiązań technologicznych, w organizacji. Współpraca podczas wydarzenia, ma na celu umożliwienie Państwu zdobycia nowych kontaktów oraz nawiązania obiecujących relacji biznesowych.</span></p>\r\n<p style="font-family: Impact; font-size: 14px; text-align: center;"></p>\r\n<p style="text-align: center;" align="center">Przygotowaliśmy dla Państwa atrakcyjne pakiety, które pozwolą na swobodny wybór optymalnego rozwiązania, spełniającego oczekiwania firm, które planują skuteczne i efektywne dotarcie ze swoją ofertą produktową do osób decyzyjnych.</p>\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n<p style="text-align: left;">W celu poznania możliwości promocji firmy podczas wydarzenia oraz szczegółowej oferty, prosimy o kontakt:</p>\r\n&nbsp;\r\n<p style="text-align: left; font-family: \'Impact\'; font-size: 14px;"><span style="font-size: large;">Aneta Budner</span></p>\r\n<p style="text-align: left;"><strong><em>Project Director</em></strong></p>\r\n<p style="text-align: left;"><a href="mailto:aneta.budner@pureconferences.pl">aneta.budner@pureconferences.pl</a>\r\ntel: 500-703-863</p>\r\n<p style="text-align: left; font-family: \'Impact\'; font-size: 14px;"><span style="font-size: medium;">Olga Fijałkowska</span></p>\r\n<p style="text-align: left;"><strong><em>Asystentka ds. organizacji konferencji</em></strong></p>\r\n<p style="text-align: left;"><a href="mailto:olga.fijalkowska@pureconferences.pl">olga.fijalkowska@pureconferences.pl</a>\r\ntel: 883-653-541</p>', 'PAKIETY SPONSORSKIE', '', 'publish', 'open', 'open', '', 'pakiety-sponsorskie', '', '', '2013-09-09 09:47:55', '2013-09-09 07:47:55', '', 0, 'http://workflowtrends.pl/?page_id=111', 0, 'page', '', 0); 
